LANGUAGE_BUTTONS = {
    "ru": "üá∑üá∫ –†—É—Å—Å–∫–∏–π",
    "en": "üá¨üáß English", 
    "es": "üá™üá∏ Espa√±ol",
    "ar": "üá∏üá¶ ÿßŸÑÿπÿ±ÿ®Ÿäÿ©",
    "hi": "üáÆüá≥ ‡§π‡§ø‡§®‡•ç‡§¶‡•Ä"
}

def get_text(language: str, key: str, **kwargs) -> str:
    """–ü–æ–ª—É—á–µ–Ω–∏–µ –ø–µ—Ä–µ–≤–µ–¥–µ–Ω–Ω–æ–≥–æ —Ç–µ–∫—Å—Ç–∞"""
    if language not in LANG:
        language = "en"
    
    if key not in LANG[language]:
        # Fallback –Ω–∞ –∞–Ω–≥–ª–∏–π—Å–∫–∏–π
        if key in LANG["en"]:
            return LANG["en"][key].format(**kwargs)
        return key
    
    return LANG[language][key].format(**kwargs)

def is_rtl_language(language: str) -> bool:
    """–ü—Ä–æ–≤–µ—Ä–∫–∞, —è–≤–ª—è–µ—Ç—Å—è –ª–∏ —è–∑—ã–∫ RTL (—Å–ø—Ä–∞–≤–∞ –Ω–∞–ª–µ–≤–æ)"""
    return language == "ar"
